package chapter02;

public class Casting2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b = 10;
		int i = (int)b;
		
		
		System.out.println("i =  + "  + i );
		System.out.println("b =  + "  + b );
		
		byte i2 = 30;
		int b2 = (int)i2;
			
		System.out.println("i2 = " + i2);
		System.out.println("b2 = " + b2);
	
	}

}
